mapper.select:
select 

from evaluate
where Sno = #{sno,jdbcType=CHAR}
  and Cno = #{cno,jdbcType=CHAR}

mapper.delete:
delete from evaluate
where Sno = #{sno,jdbcType=CHAR}
  and Cno = #{cno,jdbcType=CHAR}

mapper.insert:
insert into evaluate (Sno, Cno, Edate, Econtent
  )
values (#{sno,jdbcType=CHAR}, #{cno,jdbcType=CHAR}, #{edate,jdbcType=CHAR}, #{econtent,jdbcType=CHAR}
  )

mapper.update:
update evaluate


    Edate = #{edate,jdbcType=CHAR},


    Econtent = #{econtent,jdbcType=CHAR},


where Sno = #{sno,jdbcType=CHAR}
  and Cno = #{cno,jdbcType=CHAR}

update evaluate
set Edate = #{edate,jdbcType=CHAR},
  Econtent = #{econtent,jdbcType=CHAR}
where Sno = #{sno,jdbcType=CHAR}
  and Cno = #{cno,jdbcType=CHAR}
